mutation {
  createBlock(block: {title: "{{vBlockTitle}}", type: "standalone", bId: "{{vBId}}", skillId: null, subSkillId: null, textId: "0", bigIdea: "", childrenType: "LessonPlan", linkedBlockIds: [], showInXseedOne: true}) {
    id
    title
    bId
    text {
      id
      text
    }
    bigIdea
    type
    skill {
      id
      name
    }
    subSkill {
      id
      name
    }
    linkedBlockIds
    showInXseedOne
  }
}

query($id:ID!){
  blockMap(id:$id){
    blocks{
      displaySequence
    }
  }
}

mutation {
  linkBlockToBlockMap(data: {blockMapId: {{blockMapId}}, blockId: {{vBlockId}}, sequence: {{vBlockSeqId}}, displaySequence: {{vBlockSeqId}}, quarter: 1}) {
    sequence
    displaySequence
    blockId
    blockMapId
    quarter
  }
}

mutation {
    createLessonPlan(lessonPlan: {name: "{{vIALpName}}", description: "{{vIALpDesc}}", type: "standard"}) 
    {
      id
      name
      description
      type
    }
  }

query ($id:ID!) {
    block(id:$id){
    title 
    lessonPlans
    {
        sequence
    }
}
}

mutation {
      linkLessonPlanToBlock(data: 
      {blockId: {{vBlockId}}, lessonPlanId: "{{vCreatedLpId}}", sequence: {{vSequenceId}}, displaySequence: {{vSequenceId}}
      }) 
      {
        blockId
        sequence
        displaySequence
        lessonPlanId
        }
    }

